import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import axios from "axios"; // Import axios
import Login from "./Login";
import Registration from "./Registration";
import NoSubscription from "./NoSubscription";
import App from "./App"; // Het dashboard voor ingelogde gebruikers met een actieve subscriptie

const Routing = () => {
  const [hasActiveSubscription, setHasActiveSubscription] = useState(null); // Initially null to wait for the state
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true); // Track loading state

  useEffect(() => {
    // Make an Axios request to check if the user is authenticated and their subscription status.
    axios
      .get("/api/check-subscription")
      .then((response) => {
        // Log the response for debugging purposes
        console.log("Axios response data:", response.data);

        // Check if the user has an active subscription
        setHasActiveSubscription(response.data.hasActiveSubscription);
        setIsAuthenticated(true); // Set authenticated to true if the response is successful
      })
      .catch((error) => {
        console.error("Error fetching subscription status:", error);
        setHasActiveSubscription(false); // If error, set subscription to false
        setIsAuthenticated(false); // If error, set authenticated to false
      })
      .finally(() => {
        setLoading(false); // Set loading to false once the request is done
      });
  }, []);

  // If loading is still true, show a loading state
  if (loading) {
    return <div>Loading...</div>; // Show loading message until state is determined
  }

  return (
    <Router>
      <Routes>
        {/* Route for Login */}
        <Route path="/" element={<Login />} />

        {/* Route for Registration */}
        <Route path="/register" element={<Registration />} />

        {/* Default Route - If not authenticated, go to login */}
        <Route
          path="/aplicatie"
          element={hasActiveSubscription ? <App /> : <Navigate to="/no-subscription" />}
        />

        {/* Route for No Subscription */}
        <Route path="/no-subscription" element={<NoSubscription />} />

        {/* If no route matches, go to login */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default Routing;
