import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./Login";
import Registration from "./Registration";
import TradingAssistant from "./TradingAssistant";
import ProtectedRoute from "./ProtectedRoute";

const Routing = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Simulate fetching authentication status from localStorage or an API
    const token = localStorage.getItem("authToken"); // Replace with real token logic
    setIsAuthenticated(!!token); // If a token exists, set to true
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Registration />} />
        <Route
          path="/trading-assistant"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <TradingAssistant />
            </ProtectedRoute>
          }
        />
        {/* Default route */}
        <Route path="*" element={<Login />} />
      </Routes>
    </Router>
  );
};

export default Routing;
