import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";
import Registration from "./Registration";
import NoSubscription from "./NoSubscription";
import App from "./App"; // Dashboard for users with active subscription

const Routing = () => {
  const [hasActiveSubscription, setHasActiveSubscription] = useState(null); // Dynamically set this from login
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Set this after login

  // Check if the user is already authenticated on component mount
  useEffect(() => {
    const token = localStorage.getItem("authToken");
    const subscription = localStorage.getItem("hasActiveSubscription");
    if (token && subscription === "true") {
      setIsAuthenticated(true); // User is authenticated
      setHasActiveSubscription(true); // Assume they have an active subscription
    } else {
      setIsAuthenticated(false); // User is not authenticated
      setHasActiveSubscription(false); // Assume no subscription
    }
  }, []);

  return (
    <Router>
      <Routes>
        {/* Route for Login, but if already authenticated, redirect to /aplicatie */}
        <Route
          path="/"
          element={isAuthenticated ? <Navigate to="/aplicatie" /> : <Login setHasActiveSubscription={setHasActiveSubscription} setIsAuthenticated={setIsAuthenticated} />}
        />

        {/* Route for Registration */}
        <Route path="/register" element={<Registration />} />

        {/* Default Route - If not authenticated, go to login */}
        <Route
          path="/aplicatie"
          element={hasActiveSubscription === true ? <App /> : <Navigate to="/no-subscription" />}
        />

        {/* Route for No Subscription */}
        <Route path="/no-subscription" element={<NoSubscription />} />

        {/* If no route matches, go to login */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default Routing;
