import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css";
import { API_BASE_URL } from "./apiConfig";

const Dashboard = () => {
    const [tradeParameters, setTradeParameters] = useState({
        stopLoss: 0,
        entryPrice: 0,
        takeProfit: 0,
        biasTrend: "Notrend",
    });
    const [prompt, setPrompt] = useState("");
    const [message, setMessage] = useState("");

    // Fetch data on mount
    useEffect(() => {
        const fetchData = async () => {
            try {
                const tradeResponse = await axios.get(`${API_BASE_URL}/trade-parameters`);
                setTradeParameters(tradeResponse.data);
                const promptResponse = await axios.get(`${API_BASE_URL}/api/prompt`);
                setPrompt(promptResponse.data.content);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, []);

    // Handle trade parameters update
    const handleTradeChange = (e) => {
        setTradeParameters({
            ...tradeParameters,
            [e.target.name]: e.target.value,
        });
    };

    const handleTradeSubmit = async () => {
        try {
            await axios.put("/api/trade-parameters", tradeParameters);
            setMessage("Trade parameters updated successfully!");
        } catch (error) {
            console.error("Error updating trade parameters:", error);
            setMessage("Failed to update trade parameters.");
        }
    };

    // Handle prompt update
    const handlePromptChange = (e) => {
        setPrompt(e.target.value);
    };

    const handlePromptSubmit = async () => {
        try {
            await axios.put("/api/prompt", { content: prompt });
            setMessage("Prompt updated successfully!");
        } catch (error) {
            console.error("Error updating prompt:", error);
            setMessage("Failed to update prompt.");
        }
    };

    return (
        <div className="dashboard-background">
            <div className="dashboard-container">
                <h1 className="dashboard-title">Admin Dashboard</h1>
                {message && <p className="dashboard-message">{message}</p>}

                <div className="dashboard-section">
                    <h2 className="section-title">Trade Parameters</h2>
                    <div className="input-group">
                        <label>Stop Loss:</label>
                        <input
                            type="number"
                            name="stopLoss"
                            value={tradeParameters.stopLoss}
                            onChange={handleTradeChange}
                        />
                    </div>
                    <div className="input-group">
                        <label>Entry Price:</label>
                        <input
                            type="number"
                            name="entryPrice"
                            value={tradeParameters.entryPrice}
                            onChange={handleTradeChange}
                        />
                    </div>
                    <div className="input-group">
                        <label>Take Profit:</label>
                        <input
                            type="number"
                            name="takeProfit"
                            value={tradeParameters.takeProfit}
                            onChange={handleTradeChange}
                        />
                    </div>
                    <div className="input-group">
                        <label>Bias Trend:</label>
                        <input
                            type="text"
                            name="biasTrend"
                            value={tradeParameters.biasTrend}
                            onChange={handleTradeChange}
                        />
                    </div>
                    <button className="dashboard-button" onClick={handleTradeSubmit}>
                        Save Trade Parameters
                    </button>
                </div>

                <div className="dashboard-section">
                    <h2 className="section-title">Prompt</h2>
                    <textarea
                        className="prompt-input"
                        value={prompt}
                        onChange={handlePromptChange}
                    ></textarea>
                    <button className="dashboard-button" onClick={handlePromptSubmit}>
                        Save Prompt
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
