import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { API_BASE_URL } from "./apiConfig";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
  
    try {
      const response = await axios.post(`${API_BASE_URL}/login`, { email, password });
      const { hasActiveSubscription, message } = response.data;
  
      if (response.status === 200) {
        console.log(message);
  
        // Sla de login-status op in localStorage
        localStorage.setItem("authToken", "your-auth-token");
        localStorage.setItem("hasActiveSubscription", hasActiveSubscription);
  
        // Omleiding na succesvolle login
        if (hasActiveSubscription) {
          navigate("/"); // Naar het dashboard
        } else {
          navigate("/no-subscription"); // Naar de subscriptiepagina
        }
      }
    } catch (error) {
      setError("Login failed. Please try again.");
    }
  };
  

  return (
    <div className="background">
      <div className="container login">
        <h1 className="title">Welcome Back!</h1>
        <form className="form" onSubmit={handleLogin}>
          <div className="input-container">
            <input
              type="email"
              className="input"
              placeholder="Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-container">
            <input
              type="password"
              className="input"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="options">
            <label className="remember-me">
              <input type="checkbox" /> Remember Me
            </label>
            {/* Use React Router's Link for navigation */}
            <Link to="/forgot-password" className="link">Forgot Password?</Link>
          </div>
          <button type="submit" className="button">Login</button>
          {error && <p className="error-message">{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default Login;
