const handleLogin = async (e) => {
  e.preventDefault();

  try {
    const response = await axios.post(`${API_BASE_URL}/login`, { email, password });
    const { hasActiveSubscription, message } = response.data;

    if (response.status === 200) {
      console.log(message);

      // Store authentication token and subscription status
      localStorage.setItem("authToken", "your-auth-token");
      localStorage.setItem("hasActiveSubscription", hasActiveSubscription);

      if (hasActiveSubscription) {
        navigate("/"); // Redirect to the main app
      } else {
        navigate("/no-subscription"); // Redirect to the NoSubscription page
      }
    }
  } catch (error) {
    console.error("Login failed:", error.response?.data?.error || error.message);
    setError(error.response?.data?.error || "An error occurred. Please try again.");
  }
};
