import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";
import Registration from "./Registration";
import NoSubscription from "./NoSubscription";
import App from "./App"; // Het dashboard voor ingelogde gebruikers met een actieve subscriptie
import ProtectedRoute from "./ProtectedRoute";

const Routing = () => {
  const [hasActiveSubscription, setHasActiveSubscription] = useState(null); // Use null initially
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("authToken");

    const subscription = localStorage.getItem("hasActiveSubscription") === "true";
    console.log("hasactivesub 1 " + localStorage.getItem("hasActiveSubscription"));

    setIsAuthenticated(!!token); // Only true if there is an authToken
    console.log("hasactivesub " + localStorage.getItem("hasActiveSubscription"));
    setHasActiveSubscription(subscription); // Set the subscription state
  }, []);

  if (hasActiveSubscription === null) {
    // Return null or loading state until we check the subscription and authentication
    return null;
  }

  return (
    <Router>
      <Routes>
        {/* Route voor Login */}
        <Route path="/login" element={<Login />} />

        {/* Route voor Registratie */}
        <Route path="/register" element={<Registration />} />

        {/* Default Route - If not authenticated, go to login */}
       {/* <Route path="/" element={isAuthenticated ? (hasActiveSubscription ? <App /> : <NoSubscription />) : <Navigate to="/app" />} /> */} 
        {/* Route voor Geen Subscriptie */}
        <Route path="/" element={<Login />} />
        <Route path="/aplicatie" element={hasActiveSubscription ? <App /> : <Navigate to="/no-subscription" />} />

        <Route path="/no-subscription" element={<NoSubscription />} />

        {/* If no route matches, go to login */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default Routing;
