const handleLogin = async (e) => {
  e.preventDefault();

  try {
    const response = await axios.post(`${API_BASE_URL}/login`, { email, password });
    const { hasActiveSubscription, message } = response.data;

    if (response.status === 200) {
      console.log(message);

      // Sla de login-status op in localStorage
      localStorage.setItem("authToken", "your-auth-token");
      localStorage.setItem("hasActiveSubscription", hasActiveSubscription);

      // Omleiding na succesvolle login
      if (hasActiveSubscription) {
        navigate("/"); // Naar het dashboard
      } else {
        navigate("/no-subscription"); // Naar de subscriptiepagina
      }
    }
  } catch (error) {
    setError("Login failed. Please try again.");
  }
};
