import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";
import Registration from "./Registration";
import NoSubscription from "./NoSubscription";
import App from "./App"; // Het dashboard voor ingelogde gebruikers met een actieve subscriptie
import ProtectedRoute from "./ProtectedRoute";

const Routing = () => {
  const [hasActiveSubscription, setHasActiveSubscription] = useState(null); // Start as null initially
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Check if there's a valid auth token and subscription status in localStorage
    const token = localStorage.getItem("authToken");
    const subscription = localStorage.getItem("hasActiveSubscription");

    // Set the authentication and subscription state
    setIsAuthenticated(!!token);
    setHasActiveSubscription(subscription === "true");
  }, []);

  // Wait for hasActiveSubscription to be set before rendering routes
  if (hasActiveSubscription === null) {
    return <div>Loading...</div>; // Return loading state until subscription is determined
  }

  return (
    <Router>
      <Routes>
        {/* Route voor Login */}
        <Route path="/login" element={<Login />} />

        {/* Route voor Registratie */}
        <Route path="/register" element={<Registration />} />

        {/* Default Route - If not authenticated, go to login */}
        <Route path="/" element={isAuthenticated ? (hasActiveSubscription ? <App /> : <NoSubscription />) : <Navigate to="/login" />} />

        {/* Route voor Geen Subscriptie */}
        <Route path="/no-subscription" element={<NoSubscription />} />

        {/* If no route matches, go to login */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default Routing;
