import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import axios from "axios"; // Import axios
import Login from "./Login";
import Registration from "./Registration";
import NoSubscription from "./NoSubscription";
import App from "./App"; // Het dashboard voor ingelogde gebruikers met een actieve subscriptie

const Routing = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [hasActiveSubscription, setHasActiveSubscription] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if the user is authenticated by making an API call
    const fetchUserStatus = async () => {
      try {
        const token = localStorage.getItem("authToken");
        if (!token) {
          setLoading(false);
          return; // If there's no token, skip the request
        }

        const response = await axios.get('/api/check-subscription', {
          headers: {
            Authorization: `Bearer ${token}`,
          }
        });

        // Set authentication and subscription status based on the response
        setIsAuthenticated(true);
        setHasActiveSubscription(response.data.hasActiveSubscription);
      } catch (error) {
        console.error("Error fetching user status:", error);
        setIsAuthenticated(false);
        setHasActiveSubscription(false);
      } finally {
        setLoading(false);
      }
    };

    fetchUserStatus();
  }, []);

  // If loading is true, show a loading spinner or message
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <Router>
      <Routes>
        {/* Route for Login */}
        <Route path="/login" element={<Login />} />

        {/* Route for Registration */}
        <Route path="/register" element={<Registration />} />

        {/* Default Route - If not authenticated, go to login */}
        <Route
          path="/"
          element={isAuthenticated ? (hasActiveSubscription ? <App /> : <NoSubscription />) : <Navigate to="/login" />}
        />

        {/* Route for No Subscription */}
        <Route path="/no-subscription" element={<NoSubscription />} />

        {/* If no route matches, go to login */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default Routing;
