import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./Login";
import Registration from "./Registration";
import App from "./App"; // Protected app for authenticated users
import NoSubscription from "./NoSubscription"; // Page for users without a subscription
import ProtectedRoute from "./ProtectedRoute";

const Routing = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [hasActiveSubscription, setHasActiveSubscription] = useState(false);

  useEffect(() => {
    // Check authentication and subscription status
    const token = localStorage.getItem("authToken"); // Check if user is authenticated
    const subscription = localStorage.getItem("hasActiveSubscription"); // Check subscription status

    setIsAuthenticated(!!token);
    setHasActiveSubscription(subscription === "true"); // Convert to boolean
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Registration />} />
        <Route
          path="/"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              {hasActiveSubscription ? <App /> : <NoSubscription />}
            </ProtectedRoute>
          }
        />
        <Route path="/no-subscription" element={<NoSubscription />} />
        {/* Default route redirects to login */}
        <Route path="*" element={<Login />} />
      </Routes>
    </Router>
  );
};

export default Routing;
