import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { API_BASE_URL } from "./apiConfig";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault(); // Prevent form refresh
    try {
      const response = await axios.post(`${API_BASE_URL}/login`, { email, password });
      
      if (response.status === 200) {
        const { hasActiveSubscription, message, authToken } = response.data;
  
        console.log(message);  // Log the message for debugging
  
        // Store authToken and subscription status in localStorage
        localStorage.setItem("authToken", authToken); // Ensure the authToken is set
        localStorage.setItem("hasActiveSubscription", hasActiveSubscription);
        console.log(hasActiveSubscription + "is active");  // Log the message for debugging
        // Redirect after successful login and storing data
        if (hasActiveSubscription) {
          navigate("/"); // Redirect to dashboard if subscription is active
        } else {
          navigate("/no-subscription"); // Redirect to subscription page if no active subscription
        }
      } else {
        // Handle any other non-200 responses
        setError("Something went wrong. Please try again.");
      }
    } catch (error) {
      console.error("Login failed:", error.response?.data?.error || error.message);
      setError(error.response?.data?.error || "An error occurred. Please try again.");
    }
  };
  

  return (
    <div className="background">
      <div className="shape circle"></div>
      <div className="shape square"></div>
      <div className="shape triangle"></div>
      <div className="shape star"></div>

      <div className="container login">
        <h1 className="title">Welcome Back!</h1>
        <div className="separator"></div>
        <p className="subtitle">Log in to continue enjoying our services.</p>
        <form className="form" onSubmit={handleLogin}>
          <div className="input-container">
            <input
              type="email"
              className="input"
              placeholder="Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-container">
            <input
              type="password"
              className="input"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="options">
            <label className="remember-me">
              <input type="checkbox" /> Remember Me
            </label>
            <Link to="/forgot-password" className="link">Forgot Password?</Link>
          </div>
          <button type="submit" className="button">Login</button>
          {error && <p className="error-message">{error}</p>}
        </form>
        <div className="link">
          <p>Donâ€™t have an account?</p>
          <Link to="/register" className="link">Sign Up Now</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
