import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";
import Registration from "./Registration";
import NoSubscription from "./NoSubscription";
import App from "./App"; // Het dashboard voor ingelogde gebruikers met een actieve subscriptie

const Routing = () => {
  const [hasActiveSubscription, setHasActiveSubscription] = useState(null); // Initially null to wait for the state
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("authToken");
    const subscription = localStorage.getItem("hasActiveSubscription");

    // If token exists, set as authenticated, else false
    setIsAuthenticated(!!token);

    // Set the subscription state (converting the string back to a boolean)
    setHasActiveSubscription(subscription === "true");
  }, []);

  // If state hasn't been set yet, show loading
  if (hasActiveSubscription === null) {
    return <div>Loading...</div>;
  }

  return (
    <Router>
      <Routes>
        {/* Route for Login */}
        <Route path="/login" element={<Login />} />

        {/* Route for Registration */}
        <Route path="/register" element={<Registration />} />

        {/* Default Route - If not authenticated, go to login */}
        <Route
          path="/"
          element={isAuthenticated ? (hasActiveSubscription ? <App /> : <NoSubscription />) : <Navigate to="/login" />}
        />

        {/* Route for No Subscription */}
        <Route path="/no-subscription" element={<NoSubscription />} />

        {/* If no route matches, go to login */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default Routing;
