import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./Login";
import Registration from "./Registration";
import NoSubscription from "./NoSubscription";
import ForgotPassword from "./ForgotPassword"; // Import ForgotPassword page
import App from "./App";
import ProtectedRoute from "./ProtectedRoute";

const Routing = () => {
  const isAuthenticated = !!localStorage.getItem("authToken");
  const hasActiveSubscription = localStorage.getItem("hasActiveSubscription") === "true";

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Registration />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route
          path="/"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              {hasActiveSubscription ? <App /> : <NoSubscription />}
            </ProtectedRoute>
          }
        />
        <Route path="/no-subscription" element={<NoSubscription />} />
        <Route path="*" element={<Login />} />
      </Routes>
    </Router>
  );
};

export default Routing;
